const silent = (
  process.argv.some((arg) => {
    return arg === '--silent';
  })
  || (
    process.env
    && process.env.npm_config_loglevel
    && process.env.npm_config_loglevel === 'silent'
  )
);

module.exports = (...args) => {
  if (silent) {
    return;
  }
  /* eslint-disable no-console */
  console.log(...args);
};
